import React, { Component } from "react";

class Counter extends Component {
  state = {
    value: this.props.value, //this works because we have defined counter and
    //its values (props), int he counters.jsx
    //count2: 0,
    tags: ["tag1", "tag2", "tag3"]
    // imageUrl: "https://picsum.photos/200"
  };

  styles = {
    fontSize: 50,
    fontWeight: "bold"
  };

  //this is called when an object counter is created

  // {this.state.tags.length === 0 && "Please create a new tag"}, in js, boolean and string gives second operand if it is not empty, etc how many operand you want
  render() {
    return (
      <div>
        {this.state.tags.length === 0 && "Please create a new tag"}
        <span className={this.getBadgeClasses()}>{this.formatCount()}</span>
        <button
          //here, handleIncrement is a function reference, not a function()
          onClick={() => this.handleIncrement()} //see how this is not a "method per say", since its an arrow function
          //doesnt not allow me to pass product atm
          className="btn btn-secondary btn-sm"
        >
          Increment
        </button>
        {/*{this.renderTags()}*/}
      </div>
    );
  }

  //can use this to bind, or just use an arrow function instead
  //constructor() {
  //  super();
  //  this.handleIncrement = this.handleIncrement.bind(this);
  //}

  handleIncrement = product => {
    console.log(product);
    //this.state.count += 1; THIS DOESN'T WORK IN REACT
    //must use method we inherit from component
    this.setState({ value: this.state.value + 1 });
  };

  getBadgeClasses() {
    let classes = "badge m-2 badge-"; //notice how here we're not using const, coz we want to mod it
    classes += this.state.value === 0 ? "warning" : "primary";
    return classes;
  }

  formatCount() {
    const { value } = this.state;
    return value === 0 ? "Zero" : value;
  }

  renderTags() {
    if (this.state.tags.length === 0) return <p>There are no tags!</p>;
    return (
      <ul>
        {this.state.tags.map(tag => (
          <li key={tag}>{tag}</li>
        ))}
      </ul>
    );
  }
}

export default Counter;
